                        def utxos = "["
                        for(def utxo : json){
                        def utxoStr = '''{
                        "txid":"''' + utxo.txid + '''",
                        "vout":''' + utxo.vout + ''',
                        "redeemScript":"''' + utxo.redeemScript + '''",
                        "scriptPubKey":"''' + utxo.scriptPubKey + '''",
                        "amount":"''' + utxo.amount + '''"
                        },'''
                        utxos = utxos + utxoStr
                        }
